import {
    Card,
    CardBody,
    CardTitle,
    CardSubtitle,
    CardText,
    Button
} from 'reactstrap'
import 'bootstrap/dist/css/bootstrap.min.css';
import './Posts.css'

const posts = [
    { autor: 'Pepe', tema: 'experiencias', contenido: 'ayer me tire a una madurita' },
    { autor: 'Manolete', tema: 'experiencias', contenido: 'Ayer fui de cariñosonas' }

];

const styles = {
    contenedorPosts: {
        margin: '4rem',
        padding: '1rem',
        display: 'flex',
        alignItems: 'center'
    }
}

function Posts() {
    return (
        <div style={styles.contenedorPosts}>
        {posts.map((x) => (
            <Card
            body
            className='post'
            color="dark"
            inverse
            style={{
            width: '18rem'
            }}>
            <img
                style={{borderRadius: '20px'}}
                alt="Sample"
                src="https://picsum.photos/300/200"
            />
            <CardBody>
                <CardTitle tag="h5">
                    {x.autor}
                </CardTitle>
                <CardSubtitle
                className="mb-2"
                tag="h6"
                >
                    {x.tema}
                </CardSubtitle>
                <CardText>
                    {x.contenido}
                </CardText>
                <Button>
                Button
                </Button>
            </CardBody>
            </Card>
        ))}
        </div>
    );
}

export default Posts;
